import React, {Component} from 'react';
import logo from './logo.svg';
import './App.css';



const list = [
  {
    title: 'react',
    url:'http//:react.org',
    Author: 'jordan walker',
    num_comment : 4,
    point:3,
    objectID: 0,
  },
  {
    title: 'movingMedia',
    url: 'https ://movingmedia.fr/',
    Author: 'joelPOKAM',
    num_comment : 5,
    point : 4,
    objectID:1,
  }
]

class App extends Component {

  constructor(props){
    super(props);
    this.state= {
      list:list,
    }
    this.onDismiss = this.onDismiss.bind(this);
  }
  onDismissId
  render(){
    return(
      <div className = "App" >
       {this.state.list.map(item =>
         <div key={item.objectID}>
          <span>
            <a href={item.url}>{item.title}</a>
          </span>
          <span>{item.author}</span>
          <span>{item.num_comment}</span>
          <span>{item.point}</span>
          <button onClick= {()=> this.onDismiss(item.objectID)} type="button">onDismiss</button>
          </div>

       )}
      </div>
    )
  }
}

export default App;

